    <script>
      import { Toast } from 'mint-ui';
      import axios from 'axios';
      export default{
        name:'issue',
        data(){
          return {
            imgcontent:[],
            picnum:true,
            reast:'',
            picUrl:'develop',
            anzhuoid:0,
            bpic:[],//图片放大的数组
            urltype:[], //删除操作的url
            loadList:[],
            typeList:[],
            typeId:'',
            content: '',
            blogID:''
          }
        },
        created(){
          this.getTypeList();
          console.log(111111111)
        },

        methods:{
          dete(index){
            // 可以做一些提示信息
            this.imgcontent.splice(index,1);
            this.urltype.splice(index,1);
            this.bpic.splice(index,1);
            this.loadList.splice(index,1);
          },
          imgChange(e){
            var f= document.getElementById('file').files[0]
            let typePic = f.type
            var u = navigator.userAgent;
            if(u.indexOf('Android') > -1 || u.indexOf('Linux') > -1) {
              let lit = typePic.replace("image/","");
              if(lit != "JPEG" && lit != "jpeg" && lit != "PNG" && lit != "png" && lit != "jpg" && lit != "JPG" && lit != "gif" && lit != "GIF"){
                Toast('上传图片类型有误')}
            }
            if(this.imgcontent.length >= 8){
              this.picnum = false;
              Toast('最多只能上传9张图片')
            }
            var file = document.getElementById("file")		// 获取点击的文本框
            let fileList = file.files;// 获取图片文件
            if(fileList.length>1){
              Toast("暂不支持多张图片上传")
              return;
            }else{
              for(var i = 0; i < fileList.length; i++) {// 遍历获取到的图片
                var imgUrl = window.URL.createObjectURL(file.files[i]);
                this.loadList.push(file.files[i]);
                console.log(file.files[i])
                this.imgcontent.push(imgUrl);
                if (i==8) {
                  this.picnum = false;
                }
              }
            }
          },
          shanchuan(){
            console.log(this.typeId)
            let _that=this;
            axios.post("http://localhost:8088/blog/blog/insert",{content:this.content,typeId:this.typeId,userId:localStorage.getItem("user")}).then(function (reponse) {
              _that.blogID=reponse.data.blogId;
              _that.uploadimg();
            })
          },
          uploadblog(){

          },
          uploadimg(){
            let _that=this;
            var fileList=this.loadList;
            for(var i = 0; i < this.loadList.length; i++){
              console.log(this.blogID)
              console.log(this.loadList[i]);
              var formdata=new FormData();
              formdata.append("img",fileList[i],fileList[i].name);
              formdata.append("blogId",this.blogID);
              let config = {
                headers:{'Content-Type':'multipart/form-data'}
              };
              axios.post("http://localhost:8088/blog/blogApi/upload",formdata,config).then(function (response){
                Toast("上传成功，等待管理员审核")
              })
            }
          },
          getTypeList(){
            let _that=this;
            axios.get("http://localhost:8088/blog/type/find").then(function (response){
              console.log(response.data);
              _that.typeList=response.data.typeList;

            })
          }


        }
      }
